import React from "react"
import { featured } from "../../data/Data"
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

const FeaturedCard = () => {


  const  settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  return (
    <>
    <Slider {...settings} >
      {/* <div className='content grid5 mtop'> */}
      <div>
        {featured.map((items)=>(
          // <div className='box' > 
          <div>
          <img src={items.cover} alt="" />
          <h4>{items.name}</h4>
          </div>
        ))}
        {/* {featured.map((items, index) => (
          <div className='box' key={index}>
            <img src={items.cover} alt='' />
            <h4>{items.name}</h4>
            <label>{items.total}</label>
          </div>
        ))} */}
      </div>
      </Slider>
    </>
  )
}

export default FeaturedCard
